if(msg.topic == "w" || msg.topic == "c") {
    msg.payload = "/bankSet?params="+ msg.topic + ":" + msg.payload;
    node.send([[msg],[null]]);
} else if(msg.topic == "sys") {
    msg.payload = "/" + msg.payload;
    node.send([[null],[msg]]);
} else if(msg.topic == "tempScene") {
    if(msg.payload == "3500K") {
        msg.payload = "/bankSet?params=w:255";
        node.send([[null],[msg]]);
        msg.payload = "/bankSet?params=c:0";
        node.send([[null],[msg]]);
    } else if(msg.payload == "6500K") {
        msg.payload = "/bankSet?params=c:255";
        node.send([[null],[msg]]);
        msg.payload = "/bankSet?params=w:0";
        node.send([[null],[msg]]);
    }
} else if(msg.topic == "ch") {
    msg.payload = "/ch?params=" + msg.bank + ":" + msg.ch + ":" + msg.payload;
    node.send([[msg],[null]]);
} else if(msg.topic == "scene") {
    msg.payload = "/scene?params=" + msg.payload;
    node.send([[null],[msg]]);
}

flow.set('command',true);

var timeout = setTimeout(function() {
    flow.set('command',false);
},1000)

if(msg.payload == "warm") {
    msg.payload = "/warmBank";
    return msg;
}

if(msg.payload == "cool") {
    msg.payload = "/coolBank";
    return msg;
}

var value = JSON.parse(msg.payload);
var avg = 0;
var cAvg = 0;
var wAvg = 0;
var coolArray, warmArray, temp;

if(typeof value.coolBank != "undefined") {
    coolArray = JSON.parse(value.coolBank);
    flow.set('cool',coolArray);
    for(var i=0; i<coolArray.length; i++) {
        cAvg += coolArray[i];
    }
    cAvg = cAvg / coolArray.length+1;
    msg.payload = cAvg;
    if(cAvg == 1) { cAvg = 0; }
    msg.topic = "cool";
    flow.set('coolAvg',cAvg);
    if(!flow.get('command')) { node.send([[null],[msg]]); }
}

if(typeof value.warmBank != "undefined") {
    warmArray = JSON.parse(value.warmBank);
    flow.set('warm',warmArray);
    for(var j=0; j<warmArray.length; j++) {
        wAvg += warmArray[j];
    }
    wAvg = wAvg / warmArray.length+1;
    msg.payload = wAvg;
    if(wAvg == 1) { wAvg = 0; }
    msg.topic = "warm";

    flow.set('warmAvg',wAvg);
}

wAvg = flow.get('warmAvg');
msg.warm = Number.parseInt(map(wAvg, 0, 255, 0, 100), 0) + "%";
cAvg = flow.get('coolAvg');
msg.cool = Number.parseInt(map(cAvg, 0, 255, 0, 100), 0) + "%";


if(wAvg > cAvg) {
    temp = (cAvg / wAvg) * 100;
    temp = map(temp, 0, 100, 2700, 4600)
} else if(cAvg > wAvg) {
    temp = (wAvg / cAvg) * 100;
    temp = map(temp, 0, 100, 6500, 4600);
} else if(cAvg === 0 && wAvg === 0) {
    temp = 0;
} else if(cAvg == wAvg) {
    temp = 4600;
}
temp = Number.parseInt(temp, 0);
flow.set('temp',temp);
msg.temp = temp;
node.send([[msg],[null]]);
if(!flow.get('command')) { node.send([[null],[msg]]); }

function map(value, in_min, in_max, out_min, out_max) {
  return (value - in_min) * (out_max - out_min) / (in_max - in_min) + out_min;
}

var total = 0;

if(!flow.get('command')) {
    var cool = flow.get('cool');
    for(var i=0; i<cool.length; i++) {
        msg.topic = i;
        msg.payload = cool[i];
        total += cool[i];
        node.send([[msg],[null],[null]]);
    }
    var warm = flow.get('warm');
    for(var j=0; j<warm.length; j++) {
        msg.topic = j;
        msg.payload = warm[j];
        total += warm[j];
        node.send([[null],[msg],[null]]);
    }
    if(total === 0) {
        msg.payload = "show";
        node.send([[null],[null],[msg]]);
    } else if(total > 0) {
        msg.payload = "hide";
        node.send([[null],[null],[msg]]);
    }
}
